//         setSmoothies(data);
//       }
//     };
//     fetchData();
//   }, []);


  return (
    <div>
      {fetchError && <p>{fetchError}</p>}
      {smoothies && (
        <div>
            {smoothies.map(smoothie => (
                <SmoothieCard />
            ))}
        </div>
      )}
    </div>
  );
};

export default Home;
